import React, { useEffect, useState } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faFilm } from '@fortawesome/free-solid-svg-icons';

const Home = () => {
  const [isLoading, setIsLoading] = useState(true);
  const [showList, setShowList] = useState<string[]>([]);
  const [isTrailerVisible, setIsTrailerVisible] = useState(false); // Track the visibility of the trailer

  useEffect(() => {
    // Simulate typing effect
    const typeList = async () => {
      const listItems = [
        'Search Movies that are already in Theaters, Top Rated Indian Movies, Top Rated World Wide Movies around the World.',
        'Search movies that will be coming soon to your nearby Theaters.',
        'Add your favorite movies to the favorite Movies List.',
      ];

      for (const item of listItems) {
        await new Promise((resolve) => setTimeout(resolve, 2000)); // Wait for 3 seconds
        setShowList((prev) => [...prev, item]);
      }

      setIsLoading(false);
    };

    typeList();
  }, []);

  // Function to handle the "Get Started" button click
  const handleGetStartedClick = () => {
    // Toggle the visibility of the trailer
    setIsTrailerVisible(!isTrailerVisible);
  };

  return (
    <main>
      <section className="my-5">
        <header>
          <h1 className="ms-3">
            <FontAwesomeIcon icon={faFilm} className="me-2 fa-xl" />
            Movie<span style={{ color: '#02a8c7' }}>Hub</span>
          </h1>
        </header>
        <hr />
        <p className="ms-3">
          Welcome to the Movie<span style={{ color: '#02a8c7' }}>Hub</span> App.
          Here you can
          <ul>
            <br />
            {showList.map((item, index) => (
              <li key={index}>{item}</li>
            ))}
          </ul>
          <br />
          And enjoy the Experience on Movies on the Tip.
        </p>
        {!isLoading && (
          <>
            <button onClick={handleGetStartedClick}>Get Started</button>
            {isTrailerVisible && (
              <video
                autoPlay
                muted
                loop
                src="your-trailer.mp4" // Replace with your trailer source
                style={{ width: '100%', height: 'auto' }}
              />
            )}
          </>
        )}
      </section>
    </main>
  );
};

export default Home;
