import React, { useEffect, useState } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faFilm } from '@fortawesome/free-solid-svg-icons';

const Home = () => {
  const [isLoading, setIsLoading] = useState(true);
  const [showList, setShowList] = useState<string[]>([]); // Change the initial state to an empty string array

  useEffect(() => {
    // Simulate typing effect
    const typeList = async () => {
      const listItems = [
        'Search Movies that are already in Theaters, Top Rated Indian Movies, Top Rated World Wide Movies around the World.',
        'Search movies that will be coming soon to your nearby Theaters.',
        'Add your favorite movies to the favorite Movies List.',
      ];

      for (const item of listItems) {
        for (let i = 0; i < item.length; i++) {
          await new Promise((resolve) => setTimeout(resolve, 50)); // Wait for 50 milliseconds between each letter
          setShowList((prev) => [...prev, item.slice(0, i + 1)]);
        }
        await new Promise((resolve) => setTimeout(resolve, 1000)); // Wait for 1 second after completing each item
      }

      setIsLoading(false);
    };

    typeList();
  }, []);

  return (
    <main>
      <section className="my-5">
        <header>
          <h1 className="ms-3">
            <FontAwesomeIcon icon={faFilm} className="me-2 fa-xl" />
            Movie<span style={{ color: '#02a8c7' }}>Hub</span>
          </h1>
        </header>
        <hr />
        <p className="ms-3">
          Welcome to the Movie<span style={{ color: '#02a8c7' }}>Hub</span> App.
          Here you can
          <ul>
            <br />
            {showList.map((item, index) => (
              <li key={index}>{item}</li>
            ))}
          </ul>
          <br />
          And enjoy the Experience on Movies on the Tip.
        </p>
        {!isLoading && (
          <button
            onClick={() => {
            
            }}
          >
            Get Started
          </button>
        )}
      </section>
    </main>
  );
};

export default Home;
