import { useEffect, useState } from "react";
import { RouteComponentProps } from "react-router";
import { Alert, Col, Row } from "react-bootstrap";
import { LoadingStatus } from "../../services/types";
import IMovies from "../../model/IMovies";
import LoadingIndicator from "../common/LoadingIndicator";
import { getMovieByTitle } from "../../services/movies";
import NoDataMatched from "../global/NoDataMatched";
import { Link, NavLink } from "react-router-dom";


import './MoviesDetails.css';

// import 'bootstrap/dist/css/bootstrap.min.css';
// import 'bootstrap/js/src/modal';
// import '/dist/lightbox.css';
// import '/dist/lightbox.js';
// import 'dist/js/lightbox-plus-jquery.js';

type Props = {
  moviesCategory: string;
  path: string;
};
const MoviesDetails = (props: RouteComponentProps<Props>) => {
  const [status, setStatus] = useState<LoadingStatus>("LOADING");
  const [movies, setMovies] = useState<IMovies | null>(null);
  const [error, setError] = useState<Error | null>(null);

  useEffect(() => {
    const fetchMovie = async () => {
      try {
        const data = await getMovieByTitle(
          props.match.params.moviesCategory,
          props.match.params.path
        );
        setMovies(data);
        setStatus("LOADED");
      } catch (errormsg: any) {
        setError(errormsg);
        setStatus("LOADING_ERROR");
      }
    };

    fetchMovie();
  }, [props.match.params.path, props.match.params.moviesCategory]);

  let el;

  // // Get the modal
  // var modal = document.getElementById('myModal');

  // // Get the image and insert it inside the modal - use its "alt" text as a caption
  // var img = document.getElementById('myImg');
  // var modalImg = document.getElementById("img01");
  // var captionText = document.getElementById("caption");

  switch (status) {
    case "LOADING":
      el = (
        <LoadingIndicator
          size="large"
          message="Loading Libraries. Please wait...."
        />
      );
      break;
    case "LOADED":
      if (movies === null) {
        el = <NoDataMatched />;
        break;
      }

      const {
        title,
        storyline,
        poster,
        posterurl,
        duration,
        releaseDate,
        actors,
        year,
        imdbRating,
        contentRating,
        averageRating,
        genres,
      } = movies as IMovies;

      if (title === null) {
        el = <NoDataMatched />;
        break;
      }

      const imageClick = () => {
        return <img src={`${posterurl}`} alt={title} />;
      };

      var currentImgId;

      // // Get the modal
      // var modal = document.getElementById('myModal');

      // // Get the image and insert it inside the modal - use its "alt" text as a caption
      // var img = document.getElementById('myImg');
      // var modalImg = document.getElementById("img01");
      // var captionText = document.getElementById("caption");

      // const imageOnClick = () => {
      //   modal.style.display = "block";
      //   modalImg.src = this.src;
      //   modalImg.alt = this.alt;
      //   captionText.innerHTML = this.alt;

      // }

      // img.onclick = function(){
      //     modal.style.display = "block";
      //     modalImg.src = this.src;
      //     modalImg.alt = this.alt;
      //     captionText.innerHTML = this.alt;
      // }


      // // When the user clicks on <span> (x), close the modal
      // modal.onclick = function() {
      //     img01.className += " out";
      //     setTimeout(function() {
      //        modal.style.display = "none";
      //        img01.className = "modal-content";
      //      }, 400);

      // }


      el = (
        <div>
          <NavLink to="/movies-in-theaters" activeStyle={{ color: '#02a8c7'}} style={{color: '#02a8c7', position:'absolute', alignItems:'center',top:'5%', left:'3%', transform: 'translate(-6%, -30%)', fontSize:'120%'}}>Back To Home</NavLink>
          <Row className="mx-3 my-5 m">
            <Col xs={12} lg={3} className="my-3">


            {/* <section>
              <div className="rt-container">
                  <div className="col-rt-12">
                    <div className="Scriptcontent">
                        {/* <!-- Image Zoom HTML -->    */}
                        {/* <img id="myImg" src="img/image.jpg" alt="Trolltunga, Norway" width="500" height="300" /> */}
                        {/* <img id="myImg" src={`${process.env.REACT_APP_API_BASE_URL}/img/${poster}`} alt={title} width="500" height="300" onClick={() => imageOnClick()}/>

                        {/* <!-- The Modal --> */}
                        {/* <div id="myModal" className="modal">
                          <img className="modal-content" id="img01" />
                        </div> */} 
    		              {/* <!-- End Image Zoom HTML --> */}

    		            {/* </div>
		              </div>
                </div>
            </section> */}

              <img
                src={`${process.env.REACT_APP_API_BASE_URL}/img/${poster}`}
                alt={title}
                className="w-100"
                onClick={() => imageClick()}
              />
              {/* <div>
                <img
                  src={`${process.env.REACT_APP_API_BASE_URL}/img/${poster}`}
                  alt={title}
                  className="w-100"
                  onClick={() => imageClick()}
                  data-bs-toggle="modal"
                  data-bs-target="#imageleExample"
                />
              </div>

              <div className="modal fade" id='imageExample' tabIndex={-1} aria-hidden="true">
                <div className="modal-content">
                  <div className="modal-body">
                    <button type="button" className="btn-close" data-bs-dismiss="modal" aria-label="Close"></button>

                    <img src={`${posterurl}`}
                    className="d-block w-100"></img>
                  </div>
                </div>

              </div> */}


            </Col>
            <Col xs={12} lg={8} className="my-3">
              <div>
                <h2>
                  {title} ({year})
                </h2>
              </div>
              <div className="my-4">
                <Row>
                  <Col xs={3}>Imdb Rating</Col>
                  <Col xs={8}>{imdbRating}</Col>
                </Row>
                <Row className="my-2">
                  <Col xs={3}>Content Rating</Col>
                  <Col xs={8}>{contentRating}</Col>
                </Row>
                <Row className="my-2">
                  <Col xs={3}>Average Rating</Col>
                  <Col xs={8}>{averageRating}</Col>
                </Row>
                <Row className="my-2">
                  <Col xs={3}>Duration</Col>
                  <Col xs={8}>{duration}</Col>
                </Row>
                <Row className="my-2">
                  <Col xs={3}>Genres</Col>
                  <Col xs={8}>
                    {genres.map((genres) => (
                      <span className="me-2" key={genres}>
                        {genres}
                      </span>
                    ))}
                  </Col>
                </Row>
                <Row className="my-2">
                  <Col xs={3}>Actors</Col>
                  <Col xs={8}>
                    {actors.map((actors) => (
                      <span className="me-2" key={actors}>
                        {actors}
                      </span>
                    ))}
                  </Col>
                </Row>
                <Row className="my-2">
                  <Col xs={3}>Released Date</Col>
                  <Col xs={8}>{releaseDate}</Col>
                </Row>
                <Row className="my-2">
                  <Col xs={3}>Story Line</Col>
                  <Col xs={8}>{storyline}</Col>
                </Row>
              </div>
            </Col>
          </Row>
        </div>
      );
      break;

    case "LOADING_ERROR":
      let msg = error?.message ?? "";

      if (msg?.indexOf("404") > -1) {
        el = <NoDataMatched />;
      } else {
        el = <Alert variant="danger">{error?.message}</Alert>;
      }
      break;
  }

  return el;
};

export default MoviesDetails;
