import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faFilm } from '@fortawesome/free-solid-svg-icons';
import './home.css';

const Home = () => {
    return (
        <div className='image'>
        <main>
            <section className="my-1">
                <p  className="ms-3" style={{color: 'white', fontSize: '20px', fontWeight: 'bold'}} ><br />
                    Welcome to the Movie<span style={{color: '#02a8c7'}}>Hub</span> App.
                    <hr />
                    Here you can 
                    <ul><br />
                        <li>
                            Search Movies that are already in Theaters, Top Rated Indian Movies, Top Rated World Wide Movies around the World.
                        </li><br />
                        <li>
                            Search movies that will be coming soon to your near by Theaters. 
                        </li><br />
                        <li>
                            Add your favourite movies  in the favourite Movies List.  
                        </li>
                    </ul> <br />
                    And enjoy the Experience on Movies on the Tip. 
                </p>
            </section>
        </main>
        </div>
    );
};

export default Home;
