import React from 'react';
import { faHeart } from '@fortawesome/free-solid-svg-icons';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { Button, Card } from 'react-bootstrap';
import { toast } from 'react-toastify';
import IMovies from '../../model/IMovies';
import {
  addMovie,
  removeMovieById,
  getHigestMovieId,
  getMovieByTitle,
} from '../../services/movies';
import '../App.css';


type Props = {
  movies: IMovies;
  path: string;
  onRemove: (title: string) => void;
};

const MovieListItem = ({ movies, path, onRemove }: Props) => {
  const toastTimeout = 1800;
  const isFavouritePage = path === 'favourite';

  const { title, poster } = movies;

  var toPath = `${path}/${title}`;

  const [isClicked, setIsClicked] = useState(false);

  const addMovieToFavourite = async (
    event: React.MouseEvent<HTMLButtonElement>
  ) => {
    event.preventDefault();
    try {
      const movieByTitle = await getMovieByTitle('favourite', movies.title);
      if (movieByTitle !== null) {
        toast.error('Already added to Favourites...', { autoClose: toastTimeout });
        return;
      }

      const highestId = await getHigestMovieId('favourite');
      movies.id = highestId + 1;
      await addMovie('favourite', movies);
      toast.success('Successfully added to Favourites...', { autoClose: toastTimeout });

      // Apply the 'added' class to the button and change heart color to yellow
      event.currentTarget.classList.add('added');
      event.currentTarget.querySelector('.fa-sm').classList.add('added');

      // Update the click state
      setIsClicked(true);
    } catch (errormsg: any) {
      toast.error('Not added to Favourites...', { autoClose: toastTimeout });
    }
  };

  const removeMovieFromFavourite = async (
    event: React.MouseEvent<HTMLButtonElement>
  ) => {
    event.preventDefault();
    try {
      if (movies.id === null) {
        toast.warn('Cannot find the Movie Id to remove from the favourite list...');
      }
      const data = await removeMovieById('favourite', movies.id);
      toast.success('Successfully removed from Favourites...', { autoClose: toastTimeout });

      // Apply the 'removed' class to the button and change heart color to red
      event.currentTarget.classList.add('removed');
      event.currentTarget.querySelector('.fa-sm').classList.add('removed');

      // Update the click state
      setIsClicked(true);

      onRemove(movies.title);
    } catch (errormsg: any) {
      toast.error('Unable to remove from Favourites...', { autoClose: toastTimeout });
    }
  };

  const handleCardClick = () => {
    // If the click state is true, reset the card's click effect
    if (isClicked) {
      setIsClicked(false);
      const cardElement = document.getElementById(`movie-card-${movies.id}`);
      if (cardElement) {
        cardElement.classList.remove('clicked');
      }
    }
  };

  return (
    <Card
      id={`movie-card-${movies.id}`}
      className={`my-3 zoom my-card w-100 ${isClicked ? 'clicked' : ''}`} // Apply 'clicked' class conditionally
      style={{ width: '18rem' }}
      onClick={handleCardClick}
    >
      <a href={toPath}>
        <Card.Img
          className="card-img"
          variant="top"
          src={`${process.env.REACT_APP_API_BASE_URL}/img/${poster}`}
        />
      </a>
      <Card.Body className="text-center">
        <Card.Title>
          <div>{title}</div>
        </Card.Title>
        <Card.Text className="text-center">
          <Button
            className={`btn btnClass ${isFavouritePage ? 'removed' : 'added'}`}
            hidden={isFavouritePage}
            onClick={addMovieToFavourite}
          >
            Add to favourite
            <FontAwesomeIcon icon={faHeart} className="ms-2 fa-sm" />
          </Button>
          <Button
            className={`btn btnClass ${isFavouritePage ? 'added' : 'removed'}`}
            hidden={!isFavouritePage}
            onClick={removeMovieFromFavourite}
          >
            Remove from favourite
            <FontAwesomeIcon icon={faHeart} className="ms-2 fa-sm" />
          </Button>
        </Card.Text>
      </Card.Body>
    </Card>
  );
};

export default MovieListItem;
