import React, { useEffect, useState } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faFilm } from '@fortawesome/free-solid-svg-icons';
import TrailerModal from './TrailerModal'; 
const Home = () => {
  const [isLoading, setIsLoading] = useState(true);
  const [showList, setShowList] = useState<string[]>([]);
  const [isModalOpen, setIsModalOpen] = useState(false); // State to control the modal
  const trailerUrl = 'https://www.youtube.com/embed/your-trailer-video-url'; // Replace with your trailer video URL

  useEffect(() => {
    const typeList = async () => {
      const listItems = [
        'Search Movies that are already in Theaters, Top Rated Indian Movies, Top Rated World Wide Movies around the World.',
        'Search movies that will be coming soon to your nearby Theaters.',
        'Add your favorite movies to the favorite Movies List.',
      ];

      for (const item of listItems) {
        await new Promise((resolve) => setTimeout(resolve, 1000));
        setShowList((prev) => [...prev, item]);
      }

      setIsLoading(false);
    };

    typeList();
  }, []);

  const openModal = () => {
    setIsModalOpen(true);
  };

  const closeModal = () => {
    setIsModalOpen(false);
  };

  return (
    <main>
      <section className="my-5">
        <header>
          <h1 className="ms-3">
            <FontAwesomeIcon icon={faFilm} className="me-2 fa-xl" />
            Movie<span style={{ color: '#02a8c7' }}>Hub</span>
          </h1>
        </header>
        <hr />
        <p className="ms-3">
          Welcome to the Movie<span style={{ color: '#02a8c7' }}>Hub</span> App.
          Here you can
          <ul>
            <br />
            {showList.map((item, index) => (
              <li key={index}>{item}</li>
            ))}
          </ul>
          <br />
          And enjoy the Experience on Movies on the Tip.
        </p>
        {!isLoading && (
          <div>
            <button onClick={openModal}>Watch Trailer</button>
            {isModalOpen && (
              <TrailerModal videoUrl={trailerUrl} onClose={closeModal} />
            )}
          </div>
        )}
      </section>
    </main>
  );
};

export default Home;
